def fountainActivation(locations):
    n = len(locations)
    
    ranges = []
    for index, location in enumerate(locations):
        covered_range = {}
        covered_range["start"] = max(index + 1 - location, 1)
        covered_range["end"] = min(index + 1 + location, n)
        ranges.append(abs(covered_range.get("start") - covered_range.get("end")))
    coverages = sorted(ranges, reverse=True)
    fountains = 0
    total = 0
    for cover in coverages:
        if total < n:
            total += cover + 1
            fountains += 1
            print(total, fountains)
        else:
            break
    return fountains

    
if __name__ == '__main__':
    fptr = open(os.environ['OUTPUT_PATH'], 'w')

    locations_count = int(input().strip())

    locations = []

    for _ in range(locations_count):
        locations_item = int(input().strip())
        locations.append(locations_item)

    result = fountainActivation(locations)

    fptr.write(str(result) + '\n')

    fptr.close()
